import React, { useState,useEffect} from 'react';
import ReactDOM from 'react-dom';


import reportWebVitals from './reportWebVitals';

const App = (props) => {
  const [count, setCount] = useState(0)

  const [word, setWord] = useState(props.word);
  useEffect(() => {
    document.title= count + word
  })
  const increment = () => {

      setCount(count+1)

  }
  const decrement = () => {
    setCount(count-1)
  }
  const reset = () => {
    setCount(props.count)
  }
  return (
    <div>
      <h1>This is {word} {count}</h1>
      <button onClick={increment}>+1</button>
      <button onClick={reset}>Reset</button>
      <button onClick={decrement}>-1</button>
      <input type='text' value={word} onChange={(e) => setWord(e.target.value)}></input>
    </div>
  )
}
App.defaultProps = {
  count: 0,
  word:'count'
}


const AddNotes = () => {
  //const notesData=localStorage.getItem('notes')
  //const notesData=JSON.parse(localStorage.getItem('mynotes'))
  const [title, setTitle] = useState('');
  const [notes, setNotes] = useState([]);
  const [noteBody, setNoteBody] = useState('');
  useEffect(() => {
    const notesData=JSON.parse(localStorage.getItem('mynotes'))
    if (notesData) {
      setNotes(notesData)
      console.log(notesData)
    }

  },[])
  useEffect(() => {
    localStorage.setItem('mynotes',JSON.stringify(notes))
  },[notes])


  const addnote = (e) => {
    e.preventDefault()
    //console.log(e.target.value)
    //console.log(title)
    setNotes([
      ...notes,
      {title,noteBody}
    ])
    setTitle('')
  }
  const removeNote = (title) => {
    setNotes(notes.filter((note)=>note.title!==title))
  }
  return (
    <div>
      <h1>{notes.map((note) => (
        <IterateNote note={note} key={note.title} removeNote={removeNote}/>
      )) }</h1>
    <form onSubmit={addnote}>
        <input type='text' value={title} onChange={(e) => setTitle(e.target.value)} />
        <textarea type='text' value={noteBody} onChange={(e)=>setNoteBody(e.target.value)}/>
      <button>Add Note</button>
      </form>
      </div>
  )
}
const IterateNote = ({ note, removeNote }) => {

  useEffect(() => {
    console.log("setting up")
    return () => {
      console.log("cleaning")
    }
  },[])

  return (
    <div>
          {<div><h1>{note.title}  </h1><p>{ note.noteBody}</p></div>}

          <button onClick={()=>removeNote(note.title)}>Remove</button>
        </div>
  )
}

ReactDOM.render(
  <div><AddNotes /></div>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
